import React, { useState } from 'react';
import List from './List';

const App = () => {

    const [inputvalue , setinput] = useState('');
    const [addedvalue , setaddedvalue] = useState([]);
    
    const inputevent = (event) =>{
        const value = event.target.value;

        setinput(value);
    }

    const submit = (event) => {
        event.preventDefault();
        setaddedvalue([...addedvalue ,inputvalue]);
    }


    return(
        <>
            <div className='bg'>
                <div className='container card a'>
                    <h1 className='heading'>ToDo List</h1>
                
                    <form onSubmit={submit} className="needs-validation" novalidate>
                       <div className='input' style={{float:'left'}}>
                            <input type='text' placeholder='Enter Items to add' className="form-control" onChange={inputevent} value={inputvalue} required/>
                            <div className="invalid-feedback">Please fill out this field.</div>
                        </div>
                        <div className='submitbtn'>
                            <button type="submit" className="btn btn-primary active" style={{width:'13%'}}> + </button>
                        </div>
                    </form><br/>

                    <div className='list'>
                        <ol className="list-group list-group-flush">
                            
                            {addedvalue.map(
                                function nlist(val ,index){
                                    return(
                                        <>
                                            <List listvalue={val} showindex={index+1} />
                                        </>
                                    );
                                }
                            )}
                            
                        </ol>
                    </div>
                    
                </div>
            </div>
        </>
    );
}

// Validation
<script>
{
// Disable form submissions if there are invalid fields
(function() {
  'use strict';
  window.addEventListener('load', function() {
    // Get the forms we want to add validation styles to
    var forms = document.getElementsByClassName('needs-validation');
    // Loop over them and prevent submission
    var validation = Array.prototype.filter.call(forms, function(form) {
      form.addEventListener('submit', function(event) {
        if (form.checkValidity() === false) {
          event.preventDefault();
          event.stopPropagation();
        }
        form.classList.add('was-validated');
      }, false);
    });
  }, false);
})()
}
</script>


export default App;
